//Évila Corrêa Moreira - TADS 2019

import React from "react"; // Importação da biblioteca "React"
import styled from "styled-components/native";
import { Image } from 'react-native';


// criando um componente de texto 
const Header = styled.View`
  flex-direction:row;
  background-color:#DDD;
  height:550px;
  width:400px;
  flex-wrap:wrap;
  justify-content:center;
`;

// criando um componente de texto 
const Quadrado = styled.View`
  background-color: ${props => props.cor};
  width:180px
  height:180px;
  justify-content:center;
  align-items:center;
`;


const Pagina = styled.View`
align-items:center;  
justify-content:center;
flex:1;
`;

function App() { // Função principal
  return ( // Exibe na tela
    // Estilizando um componente "View" externamente
    <Pagina>
    <Header>
     <Quadrado cor="red">
     <Image source={require('./src/imagens/foto01.jpg')} 
          style={{width: 180, height: 180, backgroundColor: '#000'}}

        />
     </Quadrado>

     <Quadrado cor="green"></Quadrado>
     <Quadrado cor="blue"></Quadrado>
     <Quadrado cor="red"></Quadrado>
     <Quadrado  style={{width: 360, height: 180, backgroundColor: '#000'}}></Quadrado>

     </Header>
    </Pagina>
  );
}

export default App;
